import React, { useState } from "react";
import { TextField } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import Axios from "axios";

export default function LogInForm() {
  const [loginUsername, setLoginUsername] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [noUserMsg, setNoUserMsg] = useState(false)
  const [noUserError, setNoUserError] = useState("")
  const [badPassword, setBadPasswordMsg] = useState(false)
  const [badPasswordError, setBadPasswordError] = useState("")

  const login = () => {
    Axios({
      method: "POST",
      data: {
        username: loginUsername.toLowerCase(),
        password: loginPassword,
      },
      withCredentials: true,
      url: "http://localhost:3001/login",
    }).then((res) => {
      if (res.data === "Successfully Authenticated") {
        window.location.href = "/";
      } else if (res.data === "No User Exists") {
        setNoUserMsg("User Not Found");
        setNoUserError(true);
      } else {
        setBadPasswordMsg("Incorrect Password");
        setBadPasswordError(true);
      }
    });
  };




  return (
    <form autoComplete="off">
      {noUserError}
      <TextField
        error={noUserError}
        helperText={noUserMsg}
        id="standard-full-width"
        label="username"
        placeholder="@jim"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        onChange={(e) => {
          setLoginUsername(e.target.value);
        }}
      />
      <TextField
        error={setBadPasswordError}
        helperText={setBadPasswordMsg}
        type="password"
        id="standard-full-width"
        label="password"
        placeholder="***********"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        onChange={(e) => {
          setLoginPassword(e.target.value);
        }}
      />
      <Button
        variant="contained"
        style={{ margin: 30, backgroundColor: "lightblue" }}
        onClick={login}
      >
        Submit
      </Button>
    </form>
  );
}
