import React, { useState } from "react";
import { TextField } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import Axios from "axios";

export default function LogInForm() {
  const [loginUsername, setLoginUsername] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [noUserMsg, setNoUserMsg] = useState('');
  const [noUserError, setNoUserError] = useState(false);
  const [user, setUser] = useState()

  const accessToken = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxMWM4ODIyNmU2Y2U1MTZmYWJiNjVkNSIsImlhdCI6MTYyOTI2MDUzNiwiZXhwIjoxNjI5NTE5NzM2fQ.g68xEGcZ0bNaeh7fYShU1K7AiA_msSbydmFZzeJhkSM'

  Axios.interceptors.request.use(
    config => {
      config.headers.authorization = `Bearer ${accessToken}`
      return config
    }
  )

  const login = () => {
    Axios({
      method: "POST",
      data: {
        username: loginUsername.toLowerCase(),
        password: loginPassword,
      },
      withCredentials: true,
      url: "http://localhost:3001/login",
    }).then((res) => {
      if (res.status === 200) {
        setUser(res.data)
        // window.location.href = "/";
      } else if (res.data === "No User Exists") {
        setNoUserMsg("User Not Found");
        setNoUserError(true);
      }
    });
  };

  return (
    <form autoComplete="off">
      <TextField
        error={noUserError}
        helperText={noUserMsg}
        id="standard-full-width"
        label="username"
        placeholder="@jim"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        onChange={(e) => {
          setLoginUsername(e.target.value);
        }}
      />
      <TextField
        type="password"
        id="standard-full-width"
        label="password"
        placeholder="***********"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        onChange={(e) => {
          setLoginPassword(e.target.value);
        }}
      />
      <Button
        variant="contained"
        style={{ margin: 30, backgroundColor: "lightblue" }}
        onClick={login}
      >
        Submit
      </Button>
    </form>
  );
}
