import React, { useState } from "react";
import { TextField } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import Axios from "axios";
import { makeStyles } from "@material-ui/core/styles";
import "./index.css";
import { useContext } from "react";
import { UserContext } from "../../Contexts/UserContext";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  input: {
    display: "none",
  },
}));

export default function NewTweet(props) {
  const classes = useStyles();
  const data = useContext(UserContext);

  const url = "https://api.cloudinary.com/v1_1/dryaxqxie/image/upload";
  const preset = "chitter";
  const [image, setImage] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const [tweet, setTweet] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [previewSource, setPreviewSource] = useState("");
  const [fullName, setFullName] = useState("");
  let newMsgTimeoutHandle = 0;

  const [bio, setBio] = useState("");

  const openModal = () => {
    if (props.link) {
      window.location.href = "/signup";
    } else {
      setIsOpen(true);
    }
  };

  const closeModal = () => {
    console.log("closed");
    setIsOpen(false);
  };

  const onChange = (e) => {
    setImage(e.target.files[0]);
    console.log(image)
  };

  const previewFile = (file) => {
    const reader = new FileReader();
    // reads the file as url to create preview
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      setPreviewSource(reader.result);
    };
  };

  const componentWillUnmount = () => {
    clearTimeout(newMsgTimeoutHandle);
  };

  const editBio = async (publicId) => {
    try {
      await Axios.post(
        "http://localhost:3001/users/" + props.username + "/update/",
        {
          bio: bio,
          fullName: fullName,
          bioPhotoId: publicId,
        },
        {
          withCredentials: true,
        }
      ).then((response) => {
        console.log(response);
        if (response.data === "Tweet Created") {
          window.location.href = "/";
        } else if (response.data !== "Tweet Created") {
          setErrorMsg("Tweet could not be created");
          clearTimeout(newMsgTimeoutHandle);
          newMsgTimeoutHandle = setTimeout(() => {
            setErrorMsg("");
            newMsgTimeoutHandle = 0;
          }, 10000);
        }
      });
    } catch (err) {
      console.error(err);
    }
  };

  // const checkValidation = async (e) => {
  //   if (!image) {
  //     let publicId = "";
  //     postTweet(publicId);
  //   } else {
  //     const formData = new FormData();
  //     formData.append("file", image);
  //     formData.append("upload_preset", preset);
  //     const res = await Axios.post(url, formData);
  //     let publicId = res.data.secure_url;
  //     postTweet(publicId);
  //   }
  // };

  const checkValidation = async (e) => {
    if (!image) {
      let publicId = "";
      editBio(publicId);
    } else {
      const formData = new FormData();
      formData.append("file", image);
      formData.append("upload_preset", preset);
      const res = await Axios.post(url, formData);
      let publicId = res.data.secure_url;
      editBio(publicId);
    }
  };

  return (
    <div className="App">
    {isOpen && (
      <>
        <div className="overlay"></div>
        <div className="modal">
          <header className="modal__header">
            <div onClick={closeModal} className="close-button">
              &times;
            </div>
          </header>
          <main className="modal__main">
            <div>
              <form>
                <input
                  type="text"
                  id="tweet"
                  name="tweet"
                  placeholder="Set Your Bio"
                  required
                  onChange={(e) => {
                    setBio(e.target.value);
                  }}
                />
                <input
                  type="text"
                  id="tweet"
                  name="tweet"
                  placeholder="Set Your Name"
                  required
                  onChange={(e) => {
                    setFullName(e.target.value);
                  }}
                />
                <div
                  className={classes.root}
                  style={{ margin: 30 }}
                  className="uploadBtn"
                >
                  <input
                    required
                    accept="image/*"
                    multiple
                    type="file"
                    onChange={onChange}
                  />
                </div>
                <button className="submitBtn" onClick={checkValidation}>
                  <p>Edit</p>
                </button>
              </form>
            </div>
          </main>
          {previewSource && (
            <div className="previewDiv">
              <img
                src={previewSource}
                alt="chosen"
                className="preview-image"
              />
            </div>
          )}
        </div>
      </>
    )}

    <button className="button" onClick={openModal}>
      Edit Bio
    </button>
  </div>
  );
}
