import { TextField } from "@material-ui/core";
import Button from '@material-ui/core/Button';

export default function SignUpForm() {

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log('submitted')
  }

  const register = async () => {
    const formData = new FormData();
    formData.append('file', image);
    formData.append('upload_preset', preset);
    const res = await Axios.post(url, formData);
    const imageUrl = res.data.secure_url;
    try {
      await Axios.post("http://localhost:3001/signup", {
        name: nameReg,
        username: usernameReg,
        password: passwordReg,
        email: emailReg,
        publicId: imageUrl,
        job: jobReg,

      },
        {
          withCredentials: true,
        }).then((response) => {
          console.log(response);
          if (response.data === "User Created") {
            window.location.href = "/login";
          } else if (response.data !== "User Created") {
            setIsMsg("User already exists, please sign in or create new account")
            clearTimeout(newMsgTimeoutHandle);
            newMsgTimeoutHandle = setTimeout(() => {
              setIsMsg("")
              newMsgTimeoutHandle = 0;
            }, 6500)
          }
        });
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit} autoComplete="off">
      <TextField
        id="standard-full-width"
        label="username"
        placeholder="@jim"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
      />
      <TextField
        type="email"
        id="standard-full-width"
        label="email"
        placeholder="jack@chitter.com"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
      />
      <TextField
        type="password"
        id="standard-full-width"
        label="password"
        placeholder="***********"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
      />
      <TextField
        type="password"
        id="standard-full-width"
        label="confirm password"
        placeholder="***********"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
      />
      <Button variant="contained" color="primary" style={{ margin: 30 }} type="submit">
        Submit
      </Button>
    </form>
  );
}
