import { useState, useEffect } from 'react';
import axios from "axios";
import Tweet from '../Tweet/'
import './index.css'

export default function Feed() {
  const [data, setData] = useState();
  const [allData, setAllData] = useState([]);

  const userId = "pp"

  const getProfilePhoto = function (userId) {
    console.log("getProfileData");
    if (!userId) {
      axios({
        method: "GET",
        withCredentials: true,
        url: "http://localhost:3001/profile",
      }).then((res) => {
        console.log(res.data);
        setData(res.data);
      });
    } else {
      axios({
        method: "GET",
        withCredentials: true,
        url: "http://localhost:3001/profile/" + userId,
      }).then((res) => {
        console.log(res.data);
        setData(res.data);
      });
    }
  };

  const getTweets = () => {
    axios("http://localhost:3001/tweets")
      .then((response) => {
        setAllData(response.data);
      })
      .catch((error) => {
        console.log("Error getting data: " + error);
      });
  }


  useEffect(() => {
    getProfilePhoto(userId);
    getTweets()
  }, []);

  return(
    <div>
            {data ? (
        <div>
          <div className="profile_wrapper">
            <ProfileWrapper
              username={data.username}
              fullName={data.fullName}
              length={data.tweets.length}
              cloudName="chitter"
              publicId={data.publicId}
            />
          </div>
          <div class="profile_content">
            {data.tweets.map((value, index) => (
              <div>
                <ProfileTweet tweet={value.body} username={value.username} publicId={data.publicId}/>
              </div>
            ))}
          </div>
        </div>
      ) : null}

      {allData.map((value, index) => (
        <div class="tweets_feed">
          <strong>hello</strong>
          <Tweet tweet={value.body} username={value.username} createdAt={value.createdAt} publicID={data.email}/>
        </div>
      ))}
    </div>
  )
}
