import React, { useEffect, useState } from "react";
import axios from "axios";
import BlueTit from "./bluetit.jpeg"
import "./index.css";
import { useParams } from "react-router-dom";
import ProfileTweet from "../../components/ProfileTweet/";
import ExploreBar from "../../components/ExploreBar";
import SignUpBar from "../../components/SignUpBar";
import ProfileWrapper from "../../components/ProfileWrapper";

export default function Profile() {
  const [data, setData] = useState();
  const [tweets, setTweets] = useState();
  const { userId } = useParams();

  const getProfileData = function (userId) {
    if (!userId) {
      axios({
        method: "GET",
        withCredentials: true,
        url: "http://localhost:3001/profile",
      }).then((res) => {
        console.log(res.data);
        setData(res.data);
      });
    } else {
      axios({
        method: "GET",
        withCredentials: true,
        url: "http://localhost:3001/profile/" + userId,
      }).then((res) => {
        // console.log(res.data);
        setData(res.data);
      });
    }
  };

  const getTweets = async () => {
    try {
      await axios.get(
        "http://localhost:3001/users/" + data.username + "/tweets/",
      ).then((response) => {
        setTweets(response.data);
        console.log(tweets);
      });
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    getProfileData(userId);
    getTweets();
  }, [userId]);

  return (
    <div className="profile_grid">
      {data ? (
        <div>
          <div className="profile_wrapper">
            <ProfileWrapper
              username={data.username}
              fullName={data.fullName}
              length={tweets ? tweets.length : 1}
              cloudName="chitter"
              publicId={data.publicId}
            />
          </div>
          <div class="profile_content">
            { data ? tweets.map((value, index) => (
              <div>
                <ProfileTweet tweet={value.text} username={value.author.username} publicId={value.author.publicId ? value.author.publicId : ""}/>
              </div>
            )) : ''}
          </div>
        </div>
      ) : "NO TWEETWS"}
      <div className="sidebar">
        <ExploreBar />
      </div>
      <div className="signupbar">
        <SignUpBar />
      </div>
    </div>
  );
}
