import React, { useState } from "react";
import { TextField } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import Axios from "axios";
import { makeStyles } from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import PhotoCamera from "@material-ui/icons/PhotoCamera";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  input: {
    display: "none",
  },
}));

export default function SignUpForm() {
  const classes = useStyles();

  const url = "https://api.cloudinary.com/v1_1/dryaxqxie/image/upload";
  const preset = "chitter";
  const [isMsg, setIsMsg] = useState('');
  const [image, setImage] = useState("");
  const [passwordReg, setPasswordReg] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [emailReg, setEmailReg] = useState("");
  const [usernameReg, setUsernameReg] = useState("");
  const [fullNameReg, setFullNameReg] = useState("");
  const strongRegex = new RegExp("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.{4,})");
  const emailRegex = new RegExp('^[a-zA-Z0-9._:$!%-]+@[a-zA-Z0-9.-]+.[a-zA-Z]$');
  const passwordCriteria = ["Password does not meet criteria:",
    "\n• Must be over 4 characters long",
    "\n• Must include numbers and letters",
    "\n• Must include at least 1 upper and lower case letter"]
  let newMsgTimeoutHandle = 0;

  const onChange = (e) => {
    setImage(e.target.files[0]);
  };


  const register = async () => {
    const formData = new FormData();
    formData.append('file', image);
    formData.append('upload_preset', preset);
    const res = await Axios.post(url, formData);
    const imageUrl = res.data.secure_url;
    try {
      await Axios.post("http://localhost:3001/signup", {
        username: usernameReg,
        fullName: fullNameReg,
        password: passwordReg,
        email: emailReg,
        publicId: imageUrl,
      },
        {
          withCredentials: true,
        }).then((response) => {
          console.log(response);
          if (response.data === "User Created") {
            window.location.href = "/login";
          } else if (response.data !== "User Created") {
            setIsMsg("User already exists, please sign in or create new account")
            clearTimeout(newMsgTimeoutHandle);
            newMsgTimeoutHandle = setTimeout(() => {
              setIsMsg("")
              newMsgTimeoutHandle = 0;
            }, 6500)
          }
        });
    } catch (err) {
      console.error(err);
    }
  };


  const checkValidation = (e) => {
    if (!emailRegex.test(emailReg)) {
      setEmailMsg("Not a valid email address")
      clearTimeout(newMsgTimeoutHandle);
      newMsgTimeoutHandle = setTimeout(() => {
        setEmailMsg("")
        newMsgTimeoutHandle = 0;
      }, 6500)
    }
    else if (!strongRegex.test(passwordReg)) {
      setNewMsg(newText)
      clearTimeout(newMsgTimeoutHandle);
      newMsgTimeoutHandle = setTimeout(() => {
        setNewMsg("")
        newMsgTimeoutHandle = 0;
      }, 6500)
    }
    else if (passwordReg !== passwordConfirmationReg) {
      setIsMsg("Passwords do not match")
      clearTimeout(newMsgTimeoutHandle);
      newMsgTimeoutHandle = setTimeout(() => {
        setIsMsg("")
        newMsgTimeoutHandle = 0;
      }, 6500)
    }
    else {
      register()
    }
  };

  return (
    <div>
    {emailMsg} {newMsg} {isMsg} {userMsg}
    <form autoComplete="off" >
      <TextField
        id="standard-full-width"
        label="username"
        placeholder="@jim"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        onChange={(e) => {
          setUsernameReg(e.target.value);
        }}
      />
      <TextField
        id="standard-full-width"
        label="full name"
        placeholder="JimBob"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        onChange={(e) => {
          setFullNameReg(e.target.value);
        }}
      />
      <TextField
        type="email"
        id="standard-full-width"
        label="email"
        placeholder="jack@chitter.com"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        onChange={(e) => {
          setEmailReg(e.target.value);
        }}
      />
      <TextField
        type="password"
        id="standard-full-width"
        label="password"
        placeholder="***********"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        onChange={(e) => {
          setPasswordReg(e.target.value);
        }}
      />
      <TextField
        type="password"
        id="standard-full-width"
        label="confirm password"
        placeholder="***********"
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        onChange={(e) => {
          setConfirmPassword(e.target.value);
        }}
      />
      <div className="text-danger">{errors.password}</div>
      <div className={classes.root} style={{ marginTop: 30 }}>
        <input
          accept="image/*"
          className={classes.input}
          id="contained-button-file"
          multiple
          type="file"
          onChange={onChange}
        />
        <label htmlFor="contained-button-file">
          <Button variant="contained" component="span">
            Upload Profile Picture
          </Button>
        </label>
        <input
          accept="image/*"
          className={classes.input}
          id="icon-button-file"
          type="file"
          onChange={onChange}
        />
        <label htmlFor="icon-button-file">
          <IconButton aria-label="upload picture" component="span">
            <PhotoCamera />
          </IconButton>
        </label>
      </div>
      <Button
        variant="contained"
        style={{ margin: 30, width: 290, backgroundColor: "lightblue" }}
        onClick={register}
      >
        Submit
      </Button>
    </form>
    </div>
  );
}
