import ExploreBar from "../../components/ExploreBar";
import SignUpBar from "../../components/SignUpBar";
import Feed from "../../components/Feed";
import { useContext, useEffect, useState } from "react";
import { UserContext } from "../../Contexts/UserContext";
import "./styles.css";

export default function Home() {
  const { dataIsLoading, data } = useContext(UserContext);

  const LoadingWheel = () => {
    return (
      <img class="loadinggif" src={process.env.PUBLIC_URL + "/img/loading.gif"} alt="Loading"/>
    )
  }
  return (
    <div className="main_grid">
    <div className="main_content">
    {dataIsLoading ? <LoadingWheel/> :  <Feed />}
    </div>
    <div className="sidebar">
      <ExploreBar />
    </div>
    <div className="signupbar">
      <SignUpBar />
      <p className="project_text">
        This is a project by{" "}
        <a className="name_text" href="www.github.com/philsmithies">
          <span>Phil Smithies.</span>
        </a>
      </p>
    </div>
  </div>

    );
}
